import { useState ,useEffect} from "react";
import { useParams } from "react-router-dom";
import TutorialRow from "./TutorialRow";

function Tutorials() {

    const [tutorials,setTutorial] = useState([]);

    useEffect(()=>{
        console.log("Inside COmponent Did Mount");
        selectTutorial();
    }, [])

    const selectTutorial = () => {
        var {topic_id} = useParams();
        var helper = new XMLHttpRequest();
        helper.onreadystatechange = () => {
            if (helper.readyState == 4 &&
                helper.status == 200) {
                debugger;
                var result = JSON.parse(helper.responseText);
                var receivedTutorial = result;
                // console.log("Setting Emps to Array received from reqres.in website..");
                setTutorial(receivedTutorial);
            }
        };
        helper.open("GET", `http://127.0.0.1:7000/users/${topic_id}`);
        helper.send();
    }

    return (<center>
            <div>
            <table className='table table-bordered myTable'>
            <thead>
                    <th>
                        <h4>Sr.No</h4>
                    </th>
                    <th>
                        <h4>Tutorial Name</h4>
                    </th>
                    <th>
                        <h4>Description</h4>
                    </th>
            </thead>

            <tbody>

                {
                    tutorials.map((tutorial) => {
                            debugger;
                        {
                            return <TutorialRow
                                key={tutorial.tutorial_id}
                                tutorial={tutorial}
                                 />;
                        }
                    })
                }
            </tbody>
        </table>
    </div>
    </center>);
}
export default Tutorials;
