function TopicRow(props)
{
    return <tr>
             <center>
             <td>
                        {props.topic.topic_id}
                    </td>
                    <td>
                        {props.topic.topic_name}
                    </td>
                    <td>
                    <button className='btn btn-danger'
                    onClick={()=>{
                     props.selectTopic(props.topic.topic_name)
                    }}>
                    Select
                    </button>
                    </td>
             </center>

           </tr>
}
export default TopicRow;
