        setProfile(copy);
    }



    return(<div className="mytable">

    <div className="form-group">
    <input type="number"
            className="form-control" 
            name="first_name"
            placeholder="Enter Eno"
            value={profile.first_name}
            onChange={onTextChange}/>
    </div>

    <div className="form-group">
    <input type="text" 
            className="form-control"
            name="last_name"
            placeholder="Enter Eno"
            value={profile.last_name}
            onChange={onTextChange}/>
    </div>

    <div className="form-group">
    <input type="text" 
            className="form-control"
            name="email"
            placeholder="Enter Eno"
            value={profile.email}
            onChange={onTextChange}/>
    </div>

    <div className="form-group">
    <input type="text" 
            className="form-control"
            name="passwd"
            placeholder="Enter Eno"
            value={profile.passwd}
            onChange={onTextChange}/>
    </div>

    <div className="form-group">
    <input type="text" 
            className="form-control"
            name="mobile"
            placeholder="Enter Eno"
            value={profile.mobile}
            onChange={onTextChange}/>
    </div>

    <button style={{margin:5}} onClick={props.update} className="btn btn-info">
        Update
    </button>
</div>)
}

