import { Link,Switch, Route } from "react-router-dom";
import Footer from "./Footer";
import Header from "./Header";
import ProtectedRoute from "./ProtectedRoute";
import { useHistory } from 'react-router-dom';
import { useEffect, useState } from "react";
import Auth from "./NavigateToFromLogin";
import Tutorials from "./Tutorials";
import Quotes from "./Quotes";

function  Launch()
{
      debugger;
      const [email, setEmail] = useState("")
      const history = useHistory(); 
      const {SignIn, SignOut} = Auth();

   useEffect(()=>{
      if(sessionStorage.getItem("email")!=null)
      {
            setEmail(sessionStorage.getItem("email"));
      }
      else
      {
            setEmail("");
      }
   }, []);

   const ShowButton =()=>{
      if(email == "")
      {
            return  <button className='btn btn-warning' onClick={SignIn}>Log In</button>
      }
      else
      {
            return  <button className='btn btn-warning' onClick={()=>{SignOut(setEmail)}}>Log out</button>
      }
   }       

    return <>
                <Header></Header>
                 <hr></hr>
                  <div style={{paddingLeft: 50}}>
                  Welcome {"  "} {email}
                  {ShowButton()}
                  </div>
                  <hr></hr>   
                 {"   |   "}
                 <Link to="/Quotes">Home</Link> 

                 {"   |   "}
                 <Link to="/topics">MyQuotes</Link> 

                 {"   |   "}
                 <Link to="/Quotes">Profile</Link> 
                 {"   |   "}
                 <hr></hr>
                   <Switch>
                         <ProtectedRoute path="/Quotes"
                               component={Quotes}setEmail={setEmail}/>
                         <ProtectedRoute path="/tutorials"
                               component={Tutorials}setEmail={setEmail}/>
                         <ProtectedRoute path="/"
                               component={Quotes} setEmail={setEmail}/>
                         <ProtectedRoute path="/" 
                               component={Quotes} setEmail={setEmail}/>
                         <ProtectedRoute path="" 
                               component={Quotes} setEmail={setEmail}/>            
                         <ProtectedRoute path="" 
                               component={Quotes} setEmail={setEmail}/>

                   </Switch>
                 <hr></hr>
                <Footer></Footer>
            </>
}
export default Launch;
