import { useState ,useEffect} from "react";
import TopicRow from "./TopicRow";

function Tutorials() {




    useEffect(()=>{
        console.log("Inside COmponent Did Mount");
        selectTopic();
    }, [])

    const selectTopic = () => {
        var helper = new XMLHttpRequest();
        helper.onreadystatechange = () => {
            if (helper.readyState == 4 &&
                helper.status == 200) {
                debugger;
                var result = JSON.parse(helper.responseText);
                var receivedTopics = result;
                // console.log("Setting Emps to Array received from reqres.in website..");
                setTopics(receivedTopics);
            }
        };
        helper.open("GET", "http://127.0.0.1:7000/users");
        helper.send();
    }

    const getTutorials=(topic_id)=>
    {
        debugger;
        var helper = new XMLHttpRequest();
        helper.onreadystatechange = () => {
            if (helper.readyState == 4 &&
                helper.status == 200) {
                debugger;
                history.push("/tutorials");
            }
        };
        helper.open("GET", "http://127.0.0.1:7000/users/"+topic_id);
        helper.setRequestHeader("Content-Type", "application/json")
        helper.send(JSON.stringify(topics));
    }

    return (<center>
            <div>
            <table className='table table-bordered myTable'>
            <thead>
                    <th>
                        <h4>Sr.No</h4>
                    </th>
                    <th>
                        <h4>Topic Name</h4>
                    </th>
                    <th>
                        <h4>Action</h4>
                    </th>
            </thead>

            <tbody>

                {
                    topics.map((topic) => {
                            debugger;
                        {
                            return <TopicRow
                                key={topic.topic_id}
                                topic={topic}
                                getTutorials={getTutorials}
                                 />;
                        }
                    })
                }
            </tbody>
        </table>
    </div>
    </center>);
}

export default Tutorials;
